(()=>{const n=document.querySelector(".recipe");!async function(){try{const s=await fetch("https://forkify-api.herokuapp.com/api/v2/recipes/5ed6604591c37cdc054bc886");let e=(await s.json()).data.recipe;const i={publisher:e.publisher,ingredients:e.ingredients,sourceUrl:e.source_url,imageUrl:e.image_url,title:e.title,servings:e.servings,cookingTime:e.cooking_time,id:e.id};console.log(i.ingredients);let c=`\n        <figure class="recipe__fig">\n          <img src="${i.imageUrl}" alt="${i.title}" class="recipe__img" />\n          <h1 class="recipe__title">\n            <span>${i.title}</span>\n          </h1>\n        </figure>\n\n        <div class="recipe__details">\n          <div class="recipe__info">\n            <svg class="recipe__info-icon">\n              <use href="src/img/icons.svg#icon-clock"></use>\n            </svg>\n            <span class="recipe__info-data recipe__info-data--minutes">${i.cookingTime}</span>\n            <span class="recipe__info-text">minutes</span>\n          </div>\n          <div class="recipe__info">\n            <svg class="recipe__info-icon">\n              <use href="src/img/icons.svg#icon-users"></use>\n            </svg>\n            <span class="recipe__info-data recipe__info-data--people">${i.servings}</span>\n            <span class="recipe__info-text">servings</span>\n\n            <div class="recipe__info-buttons">\n              <button class="btn--tiny btn--increase-servings">\n                <svg>\n                  <use href="src/img/icons.svg#icon-minus-circle"></use>\n                </svg>\n              </button>\n              <button class="btn--tiny btn--increase-servings">\n                <svg>\n                  <use href="src/img/icons.svg#icon-plus-circle"></use>\n                </svg>\n              </button>\n            </div>\n          </div>\n\n          <div class="recipe__user-generated">\n            <svg>\n              <use href="src/img/icons.svg#icon-user"></use>\n            </svg>\n          </div>\n          <button class="btn--round">\n            <svg class="">\n              <use href="src/img/icons.svg#icon-bookmark-fill"></use>\n            </svg>\n          </button>\n        </div>\n        <div class="recipe__ingredients">\n        <h2 class="heading--2">Recipe ingredients</h2>\n        <ul class="recipe__ingredient-list">\n        \n        ${i.ingredients.map((n=>`\n             <li class="recipe__ingredient">\n              <svg class="recipe__icon">\n                <use href="src/img/icons.svg#icon-check"></use>\n              </svg>\n              <div class="recipe__quantity">${n.quantity}</div>\n              <div class="recipe__description">\n                <span class="recipe__unit">${n.unit}</span>\n                ${n.description}\n              </div>\n            </li>\n            `))}\n          </ul>\n        </div>\n\n        <div class="recipe__directions">\n          <h2 class="heading--2">How to cook it</h2>\n          <p class="recipe__directions-text">\n            This recipe was carefully designed and tested by\n            <span class="recipe__publisher">The Pioneer Woman</span>. Please check out\n            directions at their website.\n          </p>\n          <a\n            class="btn--small recipe__btn"\n            href="http://thepioneerwoman.com/cooking/pasta-with-tomato-cream-sauce/"\n            target="_blank"\n          >\n            <span>Directions</span>\n            <svg class="search__icon">\n              <use href="src/img/icons.svg#icon-arrow-right"></use>\n            </svg>\n          </a>\n        </div>\n    `;n.insertAdjacentHTML("afterbegin",c)}catch(n){alert(n.message),console.log(n.message)}}()})();